import React from 'react';
import { render } from 'react-dom';
import Toast from './toast';

var toastIns = null;

export default function toast ({text = '', delay = 2000, parent = document.body}) {

    if(!toastIns) {
        var div = document.createElement('div');
        parent.appendChild(div);

        render(
            <Toast ref={(component) => {
                toastIns = component;
            }} text={text} delay={2000}/>,
            div
        );
    } else {
        toastIns.toast({text, delay});
    }

    return toastIns;
}
