export var FetchStatus = {
  NONE: 'none',
  LOADING: 'loading',
  LOADED: 'loaded',
  FAILED: 'failed'
};
export var Action = {
  FETCH_STATUS_CHANGE: 'FETCH_STATUS_CHANGE'
};
export var fetchStatusChange = function fetchStatusChange(stateKey, status, resp) {
  return {
    type: stateKey + Action.FETCH_STATUS_CHANGE,
    payload: {
      status: status,
      resp: resp
    }
  };
};