export const FetchStatus = {
    NONE: 'none',
    LOADING: 'loading',
    LOADED: 'loaded',
    FAILED: 'failed'
};

export const Action = {
    FETCH_STATUS_CHANGE: 'FETCH_STATUS_CHANGE'
};

export const fetchStatusChange = (stateKey, status, resp) => {
    return {
        type: stateKey + Action.FETCH_STATUS_CHANGE,
        payload: {
            status,
            resp
        }
    }
}