export const compareVersion = (str1, str2) => {
    let v1 = str1.split(".");
    let v2 = str2.split(".");
    let l1 = v1.length;
    let l2 = v2.length;
    for(let i=0; i<Math.max(l1, l2); i++) {
        let k1 = 0;
        if(i < l1) {
            k1 = parseInt(v1[i]);
        }

        let k2 = 0;
        if(i < l2) {
            k2 = parseInt(v2[i]);
        }

        if(k1 != k2) {
            return k1 > k2 ? 1 : -1;
        }
    }
    return 0;
}