import React from 'react';
import {customFormatNumber} from './src/number-format.js';
import expect from "expect";
import {storiesOf, action, describe, it, specs,
beforeEach, before, after, afterEach, xdescribe,
fit, xit} from "../../.storybook/facade";

storiesOf('Toolkits', module)
  .add('default', () => {
  
    const story = (<div></div>);

    specs(() => describe('customFormatNumber', function () {
        it('Should 10201023 match 1020.1万', function () {
            const result = customFormatNumber(10201023);
          expect(result).toEqual('1020.1万');
        });
      }));

      return story;
  })